CREATE VIEW FindHighCPU_Separate AS
SELECT loc_PUB_Titles.PUB_ISSNL, loc_PUB_Titles.PUB_Title, loc_PUB_Titles.LC_Class, ROUND(temp_TitleSummarySeparate.Year1Cost, 2) AS Year1Cost, ROUND(temp_TitleSummarySeparate.Year1CPU, 2) AS Year1CPU, ROUND(temp_TitleSummarySeparate.Year2CPU, 2) AS Year2CPU, ROUND(temp_TitleSummarySeparate.Year3CPU, 2) AS Year3CPU, ROUND(temp_TitleSummarySeparate.Year4CPU, 2) AS Year4CPU, ROUND((temp_TitleSummarySeparate.Year1CPU + temp_TitleSummarySeparate.Year2CPU + temp_TitleSummarySeparate.Year3CPU + temp_TitleSummarySeparate.Year4CPU) / 4, 2) AS '4YearAvg'
FROM temp_TitleSummarySeparate INNER JOIN loc_PUB_Titles ON temp_TitleSummarySeparate.PUB_ISSNL = loc_PUB_Titles.PUB_ISSNL
WHERE (((temp_TitleSummarySeparate.Year1Cost)>0) AND ((temp_TitleSummarySeparate.Year1CPU) Is Not Null))
ORDER BY temp_TitleSummarySeparate.Year1CPU DESC LIMIT 25;