CREATE VIEW SumConsortiumUsage AS
SELECT loc_COUNTER_JR1.TRLN_Year, Sum(COUNTER_Jan+COUNTER_Feb+COUNTER_Mar+COUNTER_Apr+COUNTER_May+COUNTER_Jun) AS Total_COUNTER_JanJun, Sum(COUNTER_Jul+COUNTER_Aug+COUNTER_Sep+COUNTER_Oct+COUNTER_Nov+COUNTER_Dec) AS Total_COUNTER_JulDec, Sum(COUNTER_Jan+COUNTER_Feb+COUNTER_Mar+COUNTER_Apr+COUNTER_May+COUNTER_Jun+COUNTER_Jul+COUNTER_Aug+COUNTER_Sep+COUNTER_Oct+COUNTER_Nov+COUNTER_Dec) AS Total_COUNTER__AllMonths, loc_SERSOL_Providers.SERSOL_ProvName
FROM loc_SERSOL_Providers INNER JOIN loc_COUNTER_JR1 ON loc_SERSOL_Providers.SERSOL_ProvID = loc_COUNTER_JR1.SERSOL_ProvID
GROUP BY loc_COUNTER_JR1.TRLN_Year, loc_SERSOL_Providers.SERSOL_ProvName;